INSERT INTO `civicrm_msg_template` (`msg_title`, `msg_subject`, `msg_text`, `msg_html`, `is_active`, `workflow_id`, `is_default`, `is_reserved`, `pdf_format_id`) VALUES
('claim_received', 'Your claim has been received', 'Dear Mr./Mrs. {contact.last_name},\r\n\r\nWe want to inform you that we have received your claim successfully.\r\nOur people will now check if your claim meets our criteria.\r\nThis can take up a couple of weeks.\r\n\r\nIf you have any questions regarding your claim, please contact your project officer.\r\n\r\nKinds regards,\r\n\r\nPUM Netherlands Senior Experts.', '<p>Dear Mr./Mrs. {contact.last_name},</p>\r\n\r\n<p>We want to inform you that we have received your claim successfully.<br />\r\nOur people will now check if your claim meets our criteria.<br />\r\nThis can take up a couple of weeks.</p>\r\n\r\n<p>If you have any questions regarding your claim, please contact your project officer.</p>\r\n\r\n<p>Kinds regards,</p>\r\n\r\n<p>PUM Netherlands Senior Experts.</p>', 1, NULL, 1, 0, NULL),
('claim_approved', 'Your claim has been approved', 'Dear Mr./Mrs. {contact.last_name},\r\n\r\nWe want to inform you that your claim has been approved.\r\nWe expect that you claim will be paid out within the next couple of days.\r\nIf you have any questions regarding your claim, please contact your project officer.\r\n\r\nKinds regards,\r\n\r\nPUM Netherlands Senior Experts.', '<p>Dear Mr./Mrs. {contact.last_name},</p>\r\n\r\n<p>We want to inform you that your claim has been approved.<br />\r\nWe expect that you claim will be paid out within the next couple of days.<br />\r\nIf you have any questions regarding your claim, please contact your project officer.</p>\r\n\r\n<p>Kinds regards,</p>\r\n\r\n<p>PUM Netherlands Senior Experts.</p>', 1, NULL, 1, 0, NULL),
('claim_rejected', 'Your claim has been rejected', 'Dear Mr./Mrs. {contact.last_name},\r\n\r\nWe\'re sorry to inform you that your claim has been rejected.\r\nIf you have any questions regarding the rejection of your claim, please check the reason on PUMnet under \'Claims\' or contact your project officer.\r\n\r\nKinds regards,\r\n\r\nPUM Netherlands Senior Experts.', '<p>Dear Mr./Mrs. {contact.last_name},</p>\r\n\r\n<p>We&#39;re sorry to inform you that your claim has been rejected.<br />\r\nIf you have any questions regarding the rejection of your claim, please check the reason on PUMnet under &#39;Claims&#39; or contact your project officer.</p>\r\n\r\n<p>Kinds regards,</p>\r\n\r\n<p>PUM Netherlands Senior Experts.</p>', 1, NULL, 1, 0, NULL);